import React, { lazy, Suspense } from 'react';
import { Layout, Menu, Dropdown, Icon, Breadcrumb } from 'antd';
import { connect } from 'react-redux';
import imgRefresh from '../../images/refresh.svg'
import './header.scss';

const { Header } = Layout;
const refreshBar = lazy(() => import('../refresh/refreshBar'));

class UserInfo extends React.Component {
  state = {
    visible: false,   // 菜单是否显示
  };

  render() {
    return (<div></div>)
  }
}

const HeaderBar = (props) => {
  return (
    <Header>
      	<div className="headBg">
			<div className="refresh">
				<img className="refresh-img" src={imgRefresh}/>
				<span className="">数据源每日03:00:00更新，</span>
				<span>最近一次数据同步于2020年06月22日14:51</span>
				<span>，如数据已发生变化需要立即更新，请点击“立即同步”</span>    
				<button className="refresh-btn">立即同步</button>         
			</div> 
		</div> 

    </Header>

  );
};

const mapStateToProps = (state) => {
  return {
    menuName: state.menuName
  }
};

export default connect(mapStateToProps)(HeaderBar);
