import React, { lazy, Suspense } from 'react';
import {Row, Col, Icon, Select, Input,Checkbox} from 'antd';
import { connect } from 'react-redux';
import nextStep from '../../images/next_step.png'
import './stepForm.scss';
const { Option } = Select;
const { TextArea } = Input;


class StepForm extends React.Component {

    render() {
        return (
            <div>
                <div className="step">
                    <div className="step_list checked">
                        <span><em>1</em></span>
                        <span>地址查重</span>
                    </div>
                    <img src={nextStep} />
                    <div className="step_list">
                        <span><em>2</em></span>
                        <span>填写基本信息</span>
                    </div>
                    <img src={nextStep} />
                    <div className="step_list">
                        <span><em>3</em></span>
                        <span>提交审核</span>
                    </div>
                </div>

                <div>
                    <Row className="center">
                        <Col span={12}> <span className="label"><em>*</em>公司名称：</span><span className="error_tips">富源乌兰牧场</span></Col>
                        <Col span={12}> <span className="label">公司简称：</span><span>富源乌兰</span></Col>
                    </Row>
                    <Row className="center">
                        <Col span={12}> <span className="label">关联公司名称：</span><span>富源乌兰牧场</span></Col>
                    </Row>
                </div>
            </div>
        );
    };
}
const mapStateToProps = (state) => {
  return {}
};

export default connect(mapStateToProps)(StepForm);
