import React, { lazy, Suspense } from 'react';
import { Layout, Menu, Dropdown, Icon, Breadcrumb,Radio } from 'antd';
import { connect } from 'react-redux';
import iconQuestion from '../../images/icon_question.png'
import './diolog.scss';

// const {showDetailFlag} = true;
// const {isShowNodata} = false;
class StDiolog extends React.Component {
    state = {
        value: 1,
    };

    onChange = e => {
        console.log('radio checked', e.target.value);
        this.setState({
        value: e.target.value,
        });
    };

    render() {
    return (
        <div className="show">
            <div className="diolog">
                <div className="diolog-title">
                    <img src={iconQuestion} />
                    <span>温馨提示</span>
                </div>
                <div className="content">
                    <span>信息描述，可能会很长信息描述，可能会很长。信息描述，可能会很长。信息描述，可能会很长。</span>    
                </div>
                <div className="operate">
                    <button className="cancel">取消</button>
                    <button className="ok">确定</button>    
                </div>
            </div>

            <div className="diolog">
                <div className="diolog-title">
                    <img src={iconQuestion} />
                    <span>温馨提示</span>
                </div>
                <div className="content">
                <Radio.Group onChange={this.onChange} value={this.state.value}>
                    <Radio value={1}>A</Radio>
                    <Radio value={2}>B</Radio>
                    <Radio value={3}>C</Radio>
                    <Radio value={4}>D</Radio>
                </Radio.Group>
                </div>
                <div className="operate">
                    <button className="cancel">取消</button>
                    <button className="ok">确定</button>    
                </div>
            </div>
        </div>
    );
    };
}
const mapStateToProps = (state) => {
  return {}
};

export default connect(mapStateToProps)(StDiolog);
